  $name = $_POST['name']; // input name
  $phone = $_POST['phone']; // input phone
  $message = $_POST['message']; // input phone

  $message_send = "Новый заказ на сайте".PHP_EOL."Имя: ".$name.PHP_EOL."Телефон: ".$phone.PHP_EOL."Сообщение: ".$message;

  send(-160766501,$message_send); // id беседы с заказчиком

  function send($id , $message_send) {
    $url = 'https://api.vk.com/method/messages.send';
    $params = array(
      'user_id' => $id,    // Кому отправляем
      'message' => $message_send,   // Что отправляем
      'access_token' => '5a5133c52cdf5d861a31f7c3a41f05c8ab75c332798d43ad96322342a6d0814f877aaa4aab39f22d576ea',  
      'v' => '5.62',
    );

    $result = file_get_contents($url, false, stream_context_create(array(
        'http' => array(
          'method'  => 'POST',
          'header'  => 'Content-type: application/x-www-form-urlencoded',
          'content' => http_build_query($params)
        )
    )));
  }
